/* eslint-disable no-unused-vars */
/* eslint-disable no-param-reassign */
import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

import getPublications from "../../api/publications";

const initialState = {
  currentList: [],
  pageSize: 0,
  count: 0,
  error: "",
  loading: false,
};

const fetchPublications = createAsyncThunk(
  "poke/fetchPublications",
  async (payload, _thunkAPI) => {
    const response = await getPublications(payload);
    console.log("DATA: ", response);
    return response.data;
  }
);

const baseSlice = createSlice({
  name: "base",
  initialState,
  reducers: {
    setCurrentList: (state, action) => {
      state.currentList = action.payload;
    },
    setLoading: (state, action) => {
      state.loading = action.payload;
    },
    setError: (state, action) => {
      state.error = action.payload;
    },
  },
  extraReducers: {
    [fetchPublications.fulfilled]: (state, action) => {
      state.currentList = action.payload.results;
      state.count = action.payload.info.count;
      state.pageSize = action.payload.info.count / action.payload.info.pages;
      state.error = false;
      state.loading = false;
    },
    [fetchPublications.rejected]: (state) => {
      state.currentList = [];
      state.loading = false;
      state.error = true;
    },
    [fetchPublications.pending]: (state) => {
      state.error = false;
      state.loading = true;
    },
  },
});

export const { setCurrentList, setLoading, setError } = baseSlice.actions;
export const fetchCharactersThunk = fetchPublications;
export const baseReducer = baseSlice.reducer;
