import React, { useEffect, useState } from "react";
import { Provider, useDispatch, useSelector } from "react-redux";
import "./App.css";
import { fetchPublicationsThunk } from "./store/base/baseSlice";
import store from "../src/store/index";
import TextField from "@mui/material/TextField";
import "./App.css";
import { Outlet, Link } from "react-router-dom";
import logo from "./assets/logo.svg";

// style
import Box from "@mui/material/Box";
import Chip from "@mui/material/Chip";
import Button from "@mui/material/Button";
import Grid from "@mui/material/Grid";
import Stack from "@mui/material/Stack";
import Divider from "@mui/material/Divider";
import Typography from "@mui/material/Typography";

const AppWrapper = () => {
  return (
    <Provider store={store}>
      <App />
    </Provider>
  );
};

const App = (classes) => {
  // set state for city search

  const [searchCity, setSearchCity] = useState("");

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchPublicationsThunk());
  }, []);

  const { publicationsList } = useSelector((state) => state.base);

  return (
    <div className="App">
      <header className="App-header">
        <Link to="/">
          <img src={logo} />
        </Link>
        <div>
          <div className="row">
            {publicationsList?.map((item) => (
              <Box
                sx={{
                  width: "100%",
                  maxWidth: 360,
                  bgcolor: "background.paper",
                }}
              >
                <Box sx={{ my: 3, mx: 2 }}>
                  <Grid container alignItems="center">
                    <Grid item xs>
                      <Typography gutterBottom variant="h4" component="div">
                        {item.title}
                      </Typography>
                    </Grid>
                    <Grid item>
                      <Typography gutterBottom variant="h6" component="div">
                        $4.50
                      </Typography>
                    </Grid>
                  </Grid>
                  <Typography color="text.secondary" variant="body2">
                    Pinstriped cornflower blue cotton blouse takes you on a walk
                    to the park or just down the hall.
                  </Typography>
                </Box>
                <Divider variant="middle" />
                <Box sx={{ m: 2 }}>
                  <Typography gutterBottom variant="body1">
                    Select type
                  </Typography>
                  <Stack direction="row" spacing={1}>
                    <Chip label="Extra Soft" />
                    <Chip color="primary" label="Soft" />
                    <Chip label="Medium" />
                    <Chip label="Hard" />
                  </Stack>
                </Box>
                <Box sx={{ mt: 3, ml: 1, mb: 1 }}>
                  <Button>Add to cart</Button>
                </Box>
              </Box>
            ))}
            <TextField
              onChange={(event) => setSearchCity(event.target.value)}
              id="outlined-basic"
              label="Ciudad"
              variant="outlined"
            />
            <Button variant="contained" color="warning">
              <Link to={{ pathname: "/results", search: searchCity }}>GO!</Link>
            </Button>
          </div>
          <br></br>
          <Button variant="outlined" color="warning">
            <Link to="/publicar">Publicar</Link>
          </Button>
        </div>
        <Outlet />
      </header>
    </div>
  );
};

export default AppWrapper;
