import React, { useEffect, useState } from "react";
import { Provider, useDispatch, useSelector } from "react-redux";
import "./App.css";
import { fetchPublicationsThunk } from "./store/base/baseSlice";
import store from "../src/store/index";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import "./App.css";
import { Outlet, Link } from "react-router-dom";
import logo from "./assets/logo.svg";

// style
import { styled } from "@mui/material/styles";
import Card from "@mui/material/Card";
import CardHeader from "@mui/material/CardHeader";
import CardMedia from "@mui/material/CardMedia";
import CardContent from "@mui/material/CardContent";
import CardActions from "@mui/material/CardActions";
import Collapse from "@mui/material/Collapse";
import Avatar from "@mui/material/Avatar";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import { red } from "@mui/material/colors";
import FavoriteIcon from "@mui/icons-material/Favorite";
import ShareIcon from "@mui/icons-material/Share";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import MoreVertIcon from "@mui/icons-material/MoreVert";

const styles = (muiBaseTheme) => ({
  card: {
    maxWidth: 300,
    margin: "auto",
    transition: "0.3s",
    boxShadow: "0 8px 40px -12px rgba(0,0,0,0.3)",
    "&:hover": {
      boxShadow: "0 16px 70px -12.125px rgba(0,0,0,0.3)",
    },
  },
  media: {
    paddingTop: "56.25%",
  },
  content: {
    textAlign: "left",
    padding: muiBaseTheme.spacing.unit * 3,
  },
  divider: {
    margin: `${muiBaseTheme.spacing.unit * 3}px 0`,
  },
  heading: {
    fontWeight: "bold",
  },
  subheading: {
    lineHeight: 1.8,
  },
  avatar: {
    display: "inline-block",
    border: "2px solid white",
    "&:not(:first-of-type)": {
      marginLeft: -muiBaseTheme.spacing.unit,
    },
  },
});

const AppWrapper = () => {
  return (
    <Provider store={store}>
      <App />
    </Provider>
  );
};

const App = (classes) => {
  // set state for city search

  const [searchCity, setSearchCity] = useState("");

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchPublicationsThunk());
  }, []);

  const { publicationsList } = useSelector((state) => state.base);

  return (
    <div className="App">
      <header className="App-header">
        <Link to="/">
          <img src={logo} />
        </Link>
        <div>
          <div className="row">
            <TextField
              onChange={(event) => setSearchCity(event.target.value)}
              id="outlined-basic"
              label="Ciudad"
              variant="outlined"
            />
            {publicationsList?.map((item) => (
              <div className="App">
                <Card className={classes.card}>
                  <CardMedia
                    className={classes.media}
                    image={
                      "https://image.freepik.com/free-photo/river-foggy-mountains-landscape_1204-511.jpg"
                    }
                  />
                  <CardContent className={classes.content}>
                    <Typography
                      className={"MuiTypography--heading"}
                      variant={"h6"}
                      gutterBottom
                    >
                      Nature Around Us
                    </Typography>
                    <Typography
                      className={"MuiTypography--subheading"}
                      variant={"caption"}
                    >
                      We are going to learn different kinds of species in nature
                      that live together to form amazing environment.
                    </Typography>
                    <Divider className={classes.divider} light />
                    {faces.map((face) => (
                      <Avatar
                        className={classes.avatar}
                        key={face}
                        src={face}
                      />
                    ))}
                  </CardContent>
                </Card>
              </div>
            ))}
            <Button variant="contained" color="warning">
              <Link to={{ pathname: "/results", search: searchCity }}>GO!</Link>
            </Button>
          </div>
          <br></br>
          <Button variant="outlined" color="warning">
            <Link to="/publicar">Publicar</Link>
          </Button>
        </div>
        <Outlet />
      </header>
    </div>
  );
};

export default AppWrapper;
